        password: ""
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state);
    }

    render() {
        return (
            <div className="modal">
                <form className="modalBody" onSubmit={this.handleSubmit}>
                    <p>Login</p>

                    <div className="modalBody-input">
                        <label htmlFor="name">User name</label>
